  document: Document;
}

const fileTypeIcons = {
  pdf: { icon: FileText, color: "text-red-600", bgColor: "bg-red-100" },
  word: { icon: FileText, color: "text-blue-600", bgColor: "bg-blue-100" },
  excel: { icon: FileSpreadsheet, color: "text-green-600", bgColor: "bg-green-100" },
  powerpoint: { icon: Presentation, color: "text-orange-600", bgColor: "bg-orange-100" },
  unknown: { icon: File, color: "text-gray-600", bgColor: "bg-gray-100" },
};

const formatFileSize = (bytes: number) => {
  if (bytes === 0) return '0 Bytes';
  const k = 1024;
  const sizes = ['Bytes', 'KB', 'MB', 'GB'];
  const i = Math.floor(Math.log(bytes) / Math.log(k));
  return parseFloat((bytes / Math.pow(k, i)).toFixed(2)) + ' ' + sizes[i];
};

export function DocumentCard({ document }: DocumentCardProps) {
  const [showMenu, setShowMenu] = useState(false);
  const [location, setLocation] = useLocation();
  const { toast } = useToast();
  const queryClient = useQueryClient();

  const fileTypeConfig = fileTypeIcons[document.fileType as keyof typeof fileTypeIcons] || fileTypeIcons.unknown;
  const Icon = fileTypeConfig.icon;

  const downloadMutation = useMutation({
    mutationFn: async () => {
      const response = await fetch(`/api/documents/${document.id}/download`);
      if (!response.ok) {
        throw new Error('Download failed');
      }
      return response.blob();
    },
    onSuccess: (blob) => {
      const url = window.URL.createObjectURL(blob);
      const a = window.document.createElement('a');
      a.href = url;
      a.download = document.originalName;
      window.document.body.appendChild(a);
      a.click();
      window.URL.revokeObjectURL(url);
      window.document.body.removeChild(a);

      toast({
        title: "Download started",
        description: `${document.name} is being downloaded`,
      });
    },
    onError: (error: Error) => {
      toast({
        title: "Download failed",
        description: error.message,
        variant: "destructive",
      });
    },
  });

  const handleView = () => {
    setLocation(`/documents/${document.id}`);
  };

  const handleDownload = (e: React.MouseEvent) => {
    e.stopPropagation();
    downloadMutation.mutate();
  };

  const handleShare = (e: React.MouseEvent) => {
    e.stopPropagation();
    // TODO: Implement share functionality
    toast({
      title: "Share functionality",
      description: "Document sharing will be implemented here",
    });
  };

  return (
    <Card 
      className="hover:shadow-lg transition-all duration-200 cursor-pointer group"
      onClick={handleView}
      data-testid={`document-card-${document.id}`}
    >
      <CardContent className="p-4">
        <div className="flex items-center justify-between mb-3">
          <div className={`w-10 h-10 ${fileTypeConfig.bgColor} rounded-lg flex items-center justify-center`}>
            <Icon className={`w-5 h-5 ${fileTypeConfig.color}`} />
          </div>
          <Button
            variant="ghost"
            size="sm"
            className="opacity-0 group-hover:opacity-100 transition-opacity"
            onClick={(e) => {
              e.stopPropagation();
              setShowMenu(!showMenu);
            }}
            data-testid={`button-menu-${document.id}`}
          >
            <MoreHorizontal className="w-4 h-4" />
          </Button>
        </div>

        <h3 className="font-medium text-zeolf-text mb-1 truncate" title={document.name}>
          {document.name}
        </h3>

        <p className="text-sm text-zeolf-text-secondary mb-2">
          {document.fileType.toUpperCase()} â€¢ {formatFileSize(document.fileSize)}
        </p>

        <p className="text-xs text-zeolf-text-secondary">
          Modified {formatDistance(new Date(document.updatedAt), new Date(), { addSuffix: true })}
        </p>

        {/* Category Badge */}
        <div className="mt-2">
          <Badge variant="secondary" className="text-xs">
            {document.category.replace(/_/g, ' ').replace(/\b\w/g, l => l.toUpperCase())}
          </Badge>
        </div>

        {/* Quick Actions */}
        {showMenu && (
          <div className="absolute top-12 right-4 bg-white rounded-lg shadow-lg border border-gray-200 py-1 z-10">
            <Button
              variant="ghost"
              size="sm"
              className="w-full justify-start px-3 py-2 text-sm"
              onClick={handleView}
              data-testid={`button-view-${document.id}`}
            >
              <Eye className="w-4 h-4 mr-2" />
              View
            </Button>
            <Button
              variant="ghost"
              size="sm"
              className="w-full justify-start px-3 py-2 text-sm"
              onClick={handleDownload}
              disabled={downloadMutation.isPending}
              data-testid={`button-download-${document.id}`}
            >
              <Download className="w-4 h-4 mr-2" />
              Download
            </Button>
            <Button
              variant="ghost"
              size="sm"
              className="w-full justify-start px-3 py-2 text-sm"
              onClick={handleShare}
              data-testid={`button-share-${document.id}`}
            >
              <Share className="w-4 h-4 mr-2" />
              Share
            </Button>
          </div>
        )}
      </CardContent>
    </Card>
  );
}

client/src/components/document-creator.t.content:
import { useState } from "react";
import { useForm } from "react-hook-form";
import { zodResolver } from "@hookform/resolvers/zod";
import { useMutation, useQueryClient } from "@tanstack/react-query";
import { createDocumentSchema, type CreateDocument } from "@shared/schema";
import { apiRequest } from "@/lib/queryClient";
import { useToast } from "@/hooks/use-toast";

import { Button } from "@/components/ui/button";
import { Dialog, DialogContent, DialogDescription, DialogHeader, DialogTitle } from "@/components/ui/dialog";
import { Form, FormControl, FormField, FormItem, FormLabel, FormMessage } from "@/components/ui/form";
import { Input } from "@/components/ui/input";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Textarea } from "@/components/ui/textarea";
import { Switch } from "@/components/ui/switch";
import { FileText, FileSpreadsheet, Presentation, Loader2, FolderPlus } from "lucide-react";
import { FolderCreator } from "./folder-creator";

interface DocumentCreatorProps {
  open?: boolean;
  onClose?: () => void;
  folderId?: string;
  onDocumentCreated?: () => void;
  onSuccess?: () => void;
}

const documentTypeOptions = [
  { value: "press_release", label: "Press Release", code: "PR" },
  { value: "memo", label: "Memo", code: "MEMO" },
  { value: "internal_letter", label: "Internal Letter", code: "IL" },
  { value: "external_letter", label: "External Letter", code: "EL" },
  { value: "contract", label: "Contract", code: "CON" },
  { value: "follow_up", label: "Follow-up", code: "FU" },
  { value: "report", label: "Report", code: "RPT" },
];

const fileTypeIcons = {
  word: FileText,
  excel: FileSpreadsheet,
  powerpoint: Presentation,
};

export function DocumentCreator({ open, onClose, folderId, onDocumentCreated, onSuccess }: DocumentCreatorProps) {
  const [isOpen, setIsOpen] = useState(false);
  const [showFolderCreator, setShowFolderCreator] = useState(false);
  const { toast } = useToast();
  const queryClient = useQueryClient();
  const [showRecipientFields, setShowRecipientFields] = useState(false);

  const form = useForm<CreateDocument>({
    resolver: zodResolver(createDocumentSchema),
    defaultValues: {
      documentType: "memo",
      title: "",
      fileType: "word",
      isInternal: true,
      recipientName: "",
      recipientAddress: "",
      recipientTitle: "",
      folderId: folderId || "",
    },
  });

  const createDocumentMutation = useMutation({
    mutationFn: async (data: CreateDocument) => {
      const requestData = folderId ? { ...data, folderId } : data;
      const response = await apiRequest("POST", "/api/documents/create", requestData);
      return await response.json();
    },
    onSuccess: (document) => {
      toast({
        title: "Document created successfully",
        description: `${document.name} has been created with code ${document.documentCode}`,
      });
      queryClient.invalidateQueries({ queryKey: ["/api/documents"] });
      if (folderId) {
        queryClient.invalidateQueries({ queryKey: ["/api/folders", folderId, "documents"] });
      }
      if (open !== undefined) {
        onClose?.();
      } else {
        setIsOpen(false);
      }
      form.reset();
      onDocumentCreated?.();
      onSuccess?.();
      // Redirect to document editor
      window.location.href = `/document/edit/${document.id}`;
    },
    onError: (error: Error) => {
      toast({
        title: "Failed to create document",
        description: error.message,
        variant: "destructive",
      });
    },
  });

  const watchDocumentType = form.watch("documentType");
  const watchFileType = form.watch("fileType");

  // Show recipient fields for letters
  const isLetter = watchDocumentType === "internal_letter" || watchDocumentType === "external_letter";

  const onSubmit = (data: CreateDocument) => {
    createDocumentMutation.mutate(data);
  };

  const selectedDocType = documentTypeOptions.find(opt => opt.value === watchDocumentType);
  const FileTypeIcon = fileTypeIcons[watchFileType];

  const dialogOpen = open !== undefined ? open : isOpen;
  const handleOpenChange = onClose || ((open: boolean) => setIsOpen(open));

  const DialogComponent = () => (
    <Dialog open={dialogOpen} onOpenChange={handleOpenChange}>
      {open === undefined && (
        <Button 
          className="bg-zeolf-primary hover:bg-zeolf-primary-dark"
          onClick={() => setIsOpen(true)}
          data-testid="button-create-document"
        >
          <FileText className="w-4 h-4 mr-2" />
          Create Document
        </Button>
      )}
      <DialogContent className="max-w-2xl max-h-[90vh] overflow-y-auto" data-testid="document-creator-modal">
        <DialogHeader>
          <DialogTitle className="flex items-center gap-2">
            <FileTypeIcon className="h-5 w-5" />
            Create New Document
          </DialogTitle>
          <DialogDescription>
            Create a new {selectedDocType?.label || 'document'} with automatic document coding and ZEOLF company headers.
          </DialogDescription>
        </DialogHeader>

        <Form {...form}>
          <form onSubmit={form.handleSubmit(onSubmit)} className="space-y-6">
            {/* Document Type Selection */}
            <FormField
              control={form.control}
              name="documentType"
              render={({ field }) => (
                <FormItem>
                  <FormLabel>Document Type</FormLabel>
                  <Select onValueChange={field.onChange} defaultValue={field.value}>
                    <FormControl>
                      <SelectTrigger data-testid="select-document-type">
                        <SelectValue placeholder="Select document type" />
                      </SelectTrigger>
                    </FormControl>
                    <SelectContent>
                      {documentTypeOptions.map((option) => (
                        <SelectItem key={option.value} value={option.value}>
                          <div className="flex items-center gap-2">
                            <span className="font-mono text-xs bg-gray-100 dark:bg-gray-800 px-1 rounded">
                              {option.code}
                            </span>
                            {option.label}
                          </div>
                        </SelectItem>
                      ))}
                    </SelectContent>
                  </Select>
                  <FormMessage />
                </FormItem>
              )}
            />

            {/* File Type Selection */}
            <FormField
              control={form.control}
              name="fileType"
              render={({ field }) => (
                <FormItem>
                  <FormLabel>File Format</FormLabel>
                  <Select onValueChange={field.onChange} defaultValue={field.value}>
                    <FormControl>
                      <SelectTrigger data-testid="select-file-type">
                        <SelectValue placeholder="Select file format" />
                      </SelectTrigger>
                    </FormControl>
                    <SelectContent>
                      <SelectItem value="word">
                        <div className="flex items-center gap-2">
                          <FileText className="h-4 w-4" />
                          Microsoft Word Document
                        </div>
                      </SelectItem>
                      <SelectItem value="excel">
                        <div className="flex items-center gap-2">
                          <FileSpreadsheet className="h-4 w-4" />
                          Microsoft Excel Spreadsheet
                        </div>
                      </SelectItem>
                      <SelectItem value="powerpoint">
                        <div className="flex items-center gap-2">
                          <Presentation className="h-4 w-4" />
                          Microsoft PowerPoint Presentation
                        </div>
                      </SelectItem>
                    </SelectContent>
                  </Select>
                  <FormMessage />
                </FormItem>
              )}
            />

            {/* Document Title */}
            <FormField
              control={form.control}
              name="title"
              render={({ field }) => (
                <FormItem>
                  <FormLabel>Document Title</FormLabel>
                  <FormControl>
                    <Input
                      {...field}
                      placeholder={`Enter ${selectedDocType?.label.toLowerCase()} title...`}
                      data-testid="input-document-title"
                    />
                  </FormControl>
                  <FormMessage />
                </FormItem>
              )}
            />

            {/* Internal/External Toggle for Letters */}
            {isLetter && (
              <FormField
                control={form.control}
                name="isInternal"
                render={({ field }) => (
                  <FormItem className="flex items-center justify-between p-4 border rounded-lg">
                    <div className="space-y-0.5">
                      <FormLabel>Letter Type</FormLabel>
                      <div className="text-sm text-muted-foreground">
                        {field.value ? "Internal letter (within organization)" : "External letter (to outside recipients)"}
                      </div>
                    </div>
                    <FormControl>
                      <Switch
                        checked={field.value}
                        onCheckedChange={field.onChange}
                        data-testid="switch-letter-type"
                      />
                    </FormControl>
                  </FormItem>
                )}
              />
            )}

            {/* Recipient Information for Letters */}
            {isLetter && (
              <div className="space-y-4 p-4 border rounded-lg">
                <div className="flex items-center justify-between">
                  <h3 className="font-medium">Recipient Information</h3>
                  <Button
                    type="button"
                    variant="outline"
                    size="sm"
                    onClick={() => setShowRecipientFields(!showRecipientFields)}
                    data-testid="toggle-recipient-fields"
                  >
                    {showRecipientFields ? "Hide" : "Add"} Recipient Details
                  </Button>
                </div>

                {showRecipientFields && (
                  <div className="space-y-4">
                    <FormField
                      control={form.control}
                      name="recipientName"
                      render={({ field }) => (
                        <FormItem>
                          <FormLabel>Recipient Name</FormLabel>
                          <FormControl>
                            <Input {...field} placeholder="Enter recipient's full name" data-testid="input-recipient-name" />
                          </FormControl>
                          <FormMessage />
                        </FormItem>
                      )}
                    />

                    <FormField
                      control={form.control}
                      name="recipientTitle"
                      render={({ field }) => (
                        <FormItem>
                          <FormLabel>Recipient Title/Position</FormLabel>
                          <FormControl>
                            <Input {...field} placeholder="e.g., Chief Executive Officer, Department Manager" data-testid="input-recipient-title" />
                          </FormControl>
                          <FormMessage />
                        </FormItem>
                      )}
                    />

                    <FormField
                      control={form.control}
                      name="recipientAddress"
                      render={({ field }) => (
                        <FormItem>
                          <FormLabel>Recipient Address</FormLabel>
                          <FormControl>
                            <Textarea 
                              {...field} 
                              placeholder="Enter complete mailing address"
                              rows={3}
                              data-testid="input-recipient-address"
                            />
                          </FormControl>
                          <FormMessage />
                        </FormItem>
                      )}
                    />
                  </div>
                )}
              </div>
            )}

            {/* Document Preview Info */}
            <div className="p-4 bg-muted rounded-lg">
              <h3 className="font-medium mb-2">Document Preview</h3>
              <div className="space-y-1 text-sm">
                <p><span className="font-medium">Type:</span> {selectedDocType?.label}</p>
                <p><span className="font-medium">Code:</span> {selectedDocType?.code}-2024-XXX (auto-generated)</p>
                <p><span className="font-medium">Format:</span> {watchFileType.toUpperCase()}</p>
                <p><span className="font-medium">Header:</span> ZEOLF Technology logo with creation date</p>
                <p><span className="font-medium">Footer:</span> Document type and code number</p>
              </div>
            </div>

            {/* Action Buttons */}
            <div className="flex justify-between pt-4">
              <Button
                type="button"
                variant="outline"
                onClick={() => setShowFolderCreator(true)}
                disabled={createDocumentMutation.isPending}
                className="border-blue-200 text-blue-600 hover:bg-blue-50 hover:border-blue-300"
                data-testid="button-new-folder"
              >
                <FolderPlus className="w-4 h-4 mr-2" />
                New Folder
              </Button>
              <div className="flex gap-2">
                <Button
                  type="button"
                  variant="outline"
                  onClick={() => handleOpenChange(false)}
                  disabled={createDocumentMutation.isPending}
                  data-testid="button-cancel"
                >
                  Cancel
                </Button>
                <Button
                  type="submit"
                  disabled={createDocumentMutation.isPending}
                  data-testid="button-create-document"
                >
                  {createDocumentMutation.isPending && (
                    <Loader2 className="mr-2 h-4 w-4 animate-spin" />
                  )}
                  Create Document
                </Button>
              </div>
            </div>
          </form>
        </Form>
      </DialogContent>
    </Dialog>
  );

  const handleFolderCreated = () => {
    setShowFolderCreator(false);
    // Refresh the page or update folder list
    queryClient.invalidateQueries({ queryKey: ["/api/folders"] });
    toast({
      title: "Folder created",
      description: "New folder has been created successfully.",
    });
  };

  return (
    <>
      <DialogComponent />
      <FolderCreator
        isOpen={showFolderCreator}
        onClose={() => setShowFolderCreator(false)}
        onSuccess={handleFolderCreated}
      />
    </>
  );
}

client/src/components/document-editor.ts.content:
import { useState, useEffect } from "react";
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { apiRequest } from "@/lib/queryClient";
import { useToast } from "@/hooks/use-toast";
import { Document } from "@shared/schema";

import { Button } from "@/components/ui/button";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Separator } from "@/components/ui/separator";
import { Badge } from "@/components/ui/badge";
import { 
  Save, 
  Download, 
  Upload,
  FileText, 
  FileSpreadsheet, 
  Presentation,
  Calendar,
  Code,
  Building2,
  Loader2
} from "lucide-react";

interface DocumentEditorProps {
  documentId: string;
}

interface DocumentContent {
  title: string;
  body: string;
  cells?: { [key: string]: string }; // For Excel
  slides?: Array<{ title: string; content: string }>; // For PowerPoint
}

export function DocumentEditor({ documentId }: DocumentEditorProps) {
  const { toast } = useToast();
  const queryClient = useQueryClient();
  const [content, setContent] = useState<DocumentContent>({
    title: "",
    body: "",
    cells: {},
    slides: [{ title: "", content: "" }]
  });
  const [hasUnsavedChanges, setHasUnsavedChanges] = useState(false);
  const [showUpdateModal, setShowUpdateModal] = useState(false);

  // Fetch document data
  const { data: document, isLoading } = useQuery<Document>({
    queryKey: ["/api/documents", documentId],
  });

  // Auto-save mutation
  const saveDocumentMutation = useMutation({
    mutationFn: async (updatedContent: DocumentContent) => {
      const response = await apiRequest("PUT", `/api/documents/${documentId}/content`, {
        content: updatedContent
      });
      return await response.json();
    },
    onSuccess: () => {
      setHasUnsavedChanges(false);
      toast({
        title: "Document saved",
        description: "Your changes have been saved automatically",
      });
    },
    onError: (error: Error) => {
      toast({
        title: "Failed to save",
        description: error.message,
        variant: "destructive",
      });
    },
  });

  // Export to PDF mutation
  const exportToPdfMutation = useMutation({
    mutationFn: async () => {
      const response = await apiRequest("POST", `/api/documents/${documentId}/export-pdf`);
      const blob = await response.blob();
      const url = window.URL.createObjectURL(blob);
      const a = window.document.createElement('a');
      a.href = url;
      a.download = `${document?.name || 'document'}.pdf`;
      window.document.body.appendChild(a);
      a.click();
      window.URL.revokeObjectURL(url);
      window.document.body.removeChild(a);
    },
    onSuccess: () => {
      toast({
        title: "PDF exported",
        description: "Your document has been exported successfully",
      });
    },
    onError: (error: Error) => {
      toast({
        title: "Export failed",
        description: error.message,
        variant: "destructive",
      });
    },
  });

  // Update document file mutation
  const updateDocumentMutation = useMutation({
    mutationFn: async (file: File) => {
      console.log('Starting file update mutation for:', file.name);
      const formData = new FormData();
      formData.append("file", file);
      formData.append("keepSameId", "true");

      const response = await fetch(`/api/documents/${documentId}/update`, {
        method: "PUT",
        body: formData,
        credentials: "include",
      });

      console.log('Update response status:', response.status);

      if (!response.ok) {
        const errorText = await response.text();
        console.error('Update failed:', errorText);
        let errorData;
        try {
          errorData = JSON.parse(errorText);
        } catch {
          errorData = { message: errorText };
        }
        throw new Error(errorData.message || "Update failed");
      }

      const result = await response.json();
      console.log('Update successful:', result);
      return result;
    },
    onSuccess: (data) => {
      console.log('Update mutation success, invalidating queries');
      queryClient.invalidateQueries({ queryKey: ["/api/documents", documentId] });
      queryClient.invalidateQueries({ queryKey: ["/api/documents"] });
      setShowUpdateModal(false);
      toast({
        title: "Document updated",
        description: "File has been updated successfully with the same document ID",
      });
    },
    onError: (error: any) => {
      console.error('Update mutation error:', error);
      toast({
        title: "Update failed", 
        description: error.message || "Could not update document file",
        variant: "destructive",
      });
    },
  });

  // Load initial content
  useEffect(() => {
    if (document?.content) {
      setContent(document.content as DocumentContent);
    }
  }, [document]);

  // Auto-save every 30 seconds if there are changes
  useEffect(() => {
    if (hasUnsavedChanges) {
      const timer = setTimeout(() => {
        saveDocumentMutation.mutate(content);
      }, 30000);
      return () => clearTimeout(timer);
    }
  }, [hasUnsavedChanges, content, saveDocumentMutation]);

  const handleContentChange = (field: keyof DocumentContent, value: any) => {
    setContent(prev => ({ ...prev, [field]: value }));
    setHasUnsavedChanges(true);
  };

  const handleSave = () => {
    saveDocumentMutation.mutate(content);
  };

  const handleExport = () => {
    exportToPdfMutation.mutate();
  };

  const handleFileUpdate = (event: React.ChangeEvent<HTMLInputElement>) => {
    console.log('=== UPDATE BUTTON DIAGNOSIS ===');
    console.log('File input change event triggered');
    console.log('Event target:', event.target);
    console.log('Files array:', event.target.files);
    console.log('Document ID:', documentId);

    const file = event.target.files?.[0];
    if (file) {
      console.log('âœ“ File selected successfully:');
      console.log('  - Name:', file.name);
      console.log('  - Size:', file.size, 'bytes');
      console.log('  - Type:', file.type);
      console.log('  - Last modified:', new Date(file.lastModified));
      console.log('Starting mutation...');

      updateDocumentMutation.mutate(file);

      // Reset the input so the same file can be selected again if needed
      event.target.value = '';
    } else {
      console.log('âœ— No file selected - files array is empty');
    }
    console.log('=== END DIAGNOSIS ===');
  };

  if (isLoading) {
    return (
      <div className="flex items-center justify-center h-64">
        <Loader2 className="h-8 w-8 animate-spin" />
      </div>
    );
  }

  if (!document) {
    return (
      <div className="text-center py-8">
        <p className="text-muted-foreground">Document not found</p>
      </div>
    );
  }

  const getFileIcon = () => {
    switch (document.fileType) {
      case "excel": return <FileSpreadsheet className="h-5 w-5" />;
      case "powerpoint": return <Presentation className="h-5 w-5" />;
      default: return <FileText className="h-5 w-5" />;
    }
  };

  const renderEditor = () => {
    switch (document.fileType) {
      case "word":
        return (
          <div className="space-y-4">
            <input
              type="text"
              value={content.title}
              onChange={(e) => handleContentChange("title", e.target.value)}
              className="w-full text-2xl font-bold border-none outline-none bg-transparent"
              placeholder="Document Title"
              data-testid="input-document-title"
            />
            <Separator />
            <textarea
              value={content.body}
              onChange={(e) => handleContentChange("body", e.target.value)}
              className="w-full h-96 p-4 border rounded-lg resize-none outline-none"
              placeholder="Start writing your document content..."
              data-testid="textarea-document-body"
            />
          </div>
        );

      case "excel":
        return (
          <div className="space-y-4">
            <h3 className="text-lg font-semibold">Spreadsheet Editor</h3>
            <div className="grid grid-cols-4 gap-2">
              {Array.from({ length: 20 }, (_, i) => (
                <input
                  key={i}
                  type="text"
                  value={content.cells?.[`cell_${i}`] || ""}
                  onChange={(e) => 
                    handleContentChange("cells", {
                      ...content.cells,
                      [`cell_${i}`]: e.target.value
                    })
                  }
                  className="p-2 border rounded text-sm"
                  placeholder={`Cell ${String.fromCharCode(65 + (i % 4))}${Math.floor(i / 4) + 1}`}
                  data-testid={`input-cell-${i}`}
                />
              ))}
            </div>
          </div>
        );

      case "powerpoint":
        return (
          <div className="space-y-4">
            <h3 className="text-lg font-semibold">Presentation Editor</h3>
            {content.slides?.map((slide, index) => (
              <Card key={index} className="p-4">
                <CardHeader>
                  <CardTitle className="text-lg">Slide {index + 1}</CardTitle>
                </CardHeader>
                <CardContent className="space-y-2">
                  <input
                    type="text"
                    value={slide.title}
                    onChange={(e) => {
                      const newSlides = [...(content.slides || [])];
                      newSlides[index] = { ...slide, title: e.target.value };
                      handleContentChange("slides", newSlides);
                    }}
                    className="w-full text-lg font-semibold border-none outline-none bg-transparent"
                    placeholder="Slide Title"
                    data-testid={`input-slide-title-${index}`}
                  />
                  <textarea
                    value={slide.content}
                    onChange={(e) => {
                      const newSlides = [...(content.slides || [])];
                      newSlides[index] = { ...slide, content: e.target.value };
                      handleContentChange("slides", newSlides);
                    }}
                    className="w-full h-32 p-2 border rounded resize-none"
                    placeholder="Slide Content"
                    data-testid={`textarea-slide-content-${index}`}
                  />
                </CardContent>
              </Card>
            ))}
            <Button
              variant="outline"
              onClick={() => {
                const newSlides = [...(content.slides || []), { title: "", content: "" }];
                handleContentChange("slides", newSlides);
              }}
              data-testid="button-add-slide"
            >
              Add Slide
            </Button>
          </div>
        );

      default:
        return <p>Unsupported file type for editing</p>;
    }
  };

  return (
    <div className="max-w-6xl mx-auto p-6">
      {/* Document Header */}
      <Card className="mb-6">
        <CardHeader className="pb-4">
          <div className="flex items-center justify-between">
            <div className="flex items-center gap-3">
              {getFileIcon()}
              <div>
                <h1 className="text-2xl font-bold">{document.name}</h1>
                <div className="flex items-center gap-2 text-sm text-muted-foreground">
                  <Building2 className="h-4 w-4" />
                  <span>ZEOLF Technology</span>
                  <Separator orientation="vertical" className="h-4" />
                  <Calendar className="h-4 w-4" />
                  <span>{new Date(document.createdAt).toLocaleDateString()}</span>
                  <Separator orientation="vertical" className="h-4" />
                  <Code className="h-4 w-4" />
                  <span>{document.documentCode}</span>
                </div>
              </div>
            </div>
            <div className="flex items-center gap-2">
              <Badge variant="outline">{document.category}</Badge>
              <Badge variant="secondary">{document.fileType.toUpperCase()}</Badge>
            </div>
          </div>
        </CardHeader>

        {/* Company Header */}
        <CardContent className="pt-0">
          <div className="bg-blue-50 dark:bg-blue-950 p-4 rounded-lg border-l-4 border-blue-500">
            <div className="flex items-center justify-between">
              <div>
                <h2 className="font-bold text-lg text-blue-700 dark:text-blue-300">ZEOLF TECHNOLOGY</h2>
                <p className="text-sm text-blue-600 dark:text-blue-400">Document Management System</p>
              </div>
              <div className="text-right text-sm text-blue-600 dark:text-blue-400">
                <p>Created: {new Date(document.createdAt).toLocaleDateString()}</p>
                <p>Code: {document.documentCode}</p>
              </div>
            </div>
          </div>
        </CardContent>
      </Card>

      {/* Action Buttons */}
      <div className="flex items-center justify-between mb-6">
        <div className="flex items-center gap-2">
          {hasUnsavedChanges && (
            <Badge variant="secondary" className="text-orange-600">
              Unsaved Changes
            </Badge>
          )}
          {saveDocumentMutation.isPending && (
            <Badge variant="secondary" className="text-blue-600">
              <Loader2 className="h-3 w-3 animate-spin mr-1" />
              Saving...
            </Badge>
          )}
        </div>

        <div className="flex gap-2">
          <Button
            variant="outline"
            onClick={handleSave}
            disabled={saveDocumentMutation.isPending || !hasUnsavedChanges}
            data-testid="button-save-document"
          >
            {saveDocumentMutation.isPending ? (
              <Loader2 className="mr-2 h-4 w-4 animate-spin" />
            ) : (
              <Save className="mr-2 h-4 w-4" />
            )}
            Save
          </Button>

          {/* Diagnostic test button */}
          <Button
            variant="outline"
            className="border-red-200 text-red-600 hover:bg-red-50"
            onClick={() => {
              console.log('ðŸ”§ DIAGNOSTIC TEST BUTTON CLICKED');
              console.log('Document ID:', documentId);
              console.log('Mutation pending:', updateDocumentMutation.isPending);
              console.log('Document object:', document);
              alert('Diagnostic test - check console for details');
            }}
          >
            Test
          </Button>

          <label 
            className="relative cursor-pointer inline-block"
            onClick={() => console.log('âœ“ Label clicked - should trigger file dialog')}
          >
            <div className="inline-flex items-center justify-center whitespace-nowrap rounded-md text-sm font-medium ring-offset-background transition-colors focus-visible:outline-none focus-visible:ring-2 focus-visible:ring-ring focus-visible:ring-offset-2 disabled:pointer-events-none disabled:opacity-50 border border-blue-200 bg-background hover:bg-blue-50 hover:border-blue-300 h-10 px-4 py-2 text-blue-600">
              {updateDocumentMutation.isPending ? (
                <Loader2 className="mr-2 h-4 w-4 animate-spin" />
              ) : (
                <Upload className="mr-2 h-4 w-4" />
              )}
              Update
            </div>
            <input
              type="file"
              onChange={handleFileUpdate}
              onClick={() => console.log('âœ“ File input clicked')}
              className="absolute inset-0 w-full h-full opacity-0 cursor-pointer"
              accept=".pdf,.doc,.docx,.xls,.xlsx,.ppt,.pptx"
              data-testid="input-file-update"
              disabled={updateDocumentMutation.isPending}
              style={{ zIndex: 10 }}
            />
          </label>

          <Button
            onClick={handleExport}
            disabled={exportToPdfMutation.isPending}
            data-testid="button-export-pdf"
          >
            {exportToPdfMutation.isPending ? (
              <Loader2 className="mr-2 h-4 w-4 animate-spin" />
            ) : (
              <Download className="mr-2 h-4 w-4" />
            )}
            Export PDF
          </Button>
        </div>
      </div>

      {/* Document Editor */}
      <Card>
        <CardContent className="p-6">
          {renderEditor()}
        </CardContent>
      </Card>

      {/* Document Footer Preview */}
      <Card className="mt-6">
        <CardContent className="p-4">
          <div className="bg-gray-50 dark:bg-gray-900 p-4 rounded border-t-2 border-gray-300">
            <div className="flex items-center justify-between text-sm text-gray-600 dark:text-gray-400">
              <div>
                <p className="font-medium">ZEOLF Technology - {document.category.replace('_', ' ').toUpperCase()}</p>
              </div>
              <div className="text-right">
                <p>Document Code: {document.documentCode}</p>
                <p>Generated: {new Date().toLocaleDateString()}</p>
              </div>
            </div>
          </div>
        </CardContent>
      </Card>
    </div>
  );
}

client/src/components/file-library.tsx.content:
import { useState } from "react";
import { useQuery } from "@tanstack/react-query";
import { useLocation } from "wouter";
import { 
  Folder, 
  FolderOpen, 
  Lock, 
  Unlock,
  Search,
  Grid3X3,
  List,
  Clock,
  Users,
  Plus
} from "lucide-react";

import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import { getQueryFn } from "@/lib/queryClient";
import { FolderCreator } from "./folder-creator";
import type { Folder as FolderType } from "@shared/schema";

export function FileLibrary() {
  const [, navigate] = useLocation();
  const [searchQuery, setSearchQuery] = useState("");
  const [viewMode, setViewMode] = useState<"grid" | "list">("grid");
  const [showFolderCreator, setShowFolderCreator] = useState(false);

  // Fetch all folders
  const { data: folders = [], isLoading } = useQuery<FolderType[]>({
    queryKey: ["/api/folders"],
    queryFn: async ({ queryKey }) => {
      const res = await fetch(queryKey.join("/") as string, {
        credentials: "include",
      });
      if (res.status === 401) return [];
      if (!res.ok) throw new Error(`${res.status}: ${res.statusText}`);
      return await res.json();
    },
  });

  // Filter folders based on search
  const filteredFolders = folders.filter(folder =>
    folder.name.toLowerCase().includes(searchQuery.toLowerCase()) ||
    (folder.description && folder.description.toLowerCase().includes(searchQuery.toLowerCase()))
  );

  const handleFolderClick = (folderId: string) => {
    navigate(`/folders/${folderId}`);
  };

  if (isLoading) {
    return (
      <div className="space-y-4">
        <div className="flex items-center justify-between">
          <div className="h-8 w-32 bg-gray-200 rounded animate-pulse"></div>
          <div className="h-10 w-32 bg-gray-200 rounded animate-pulse"></div>
        </div>
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
          {[...Array(6)].map((_, i) => (
            <div key={i} className="animate-pulse">
              <div className="bg-white rounded-lg shadow-sm border p-4">
                <div className="h-4 bg-gray-200 rounded w-3/4 mb-2"></div>
                <div className="h-3 bg-gray-200 rounded w-1/2"></div>
              </div>
            </div>
          ))}
        </div>
      </div>
    );
  }

  return (
    <div className="space-y-6">
      {/* Header */}
      <div className="flex items-center justify-between">
        <div>
          <h2 className="text-2xl font-bold text-zeolf-text-primary">Document Folders</h2>
          <p className="text-zeolf-text-secondary">
            Organize your documents in secure folders
          </p>
        </div>
        <Button
          onClick={() => setShowFolderCreator(true)}
          className="bg-zeolf-blue hover:bg-zeolf-blue-dark"
          data-testid="button-create-folder-plus"
        >
          <Plus className="w-4 h-4" />
        </Button>
      </div>

      {/* Search and View Controls */}
      <div className="flex items-center justify-between gap-4">
        <div className="relative flex-1 max-w-md">
          <Search className="w-4 h-4 absolute left-3 top-1/2 transform -translate-y-1/2 text-gray-400" />
          <Input
            placeholder="Search folders..."
            value={searchQuery}
            onChange={(e) => setSearchQuery(e.target.value)}
            className="pl-10"
            data-testid="input-search-folders"
          />
        </div>
        <div className="flex items-center gap-2">
          <Button
            variant={viewMode === "grid" ? "default" : "outline"}
            size="sm"
            onClick={() => setViewMode("grid")}
            data-testid="button-grid-view"
          >
            <Grid3X3 className="w-4 h-4" />
          </Button>
          <Button
            variant={viewMode === "list" ? "default" : "outline"}
            size="sm"
            onClick={() => setViewMode("list")}
            data-testid="button-list-view"
          >
            <List className="w-4 h-4" />
          </Button>
        </div>
      </div>

      {/* Folder Grid/List */}
      {filteredFolders.length === 0 ? (
        <div className="text-center py-12">
          <Folder className="w-16 h-16 mx-auto text-gray-400 mb-4" />
          <h3 className="text-xl font-semibold text-gray-900 mb-2">
            {searchQuery ? "No folders found" : "No folders yet"}
          </h3>
          <p className="text-gray-600 mb-4">
            {searchQuery 
              ? "Try adjusting your search terms"
              : "Create your first folder to organize your documents"}
          </p>
          {!searchQuery && <FolderCreator />}
        </div>
      ) : (
        <div className={viewMode === "grid" 
          ? "grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 xl:grid-cols-4 gap-4" 
          : "space-y-3"
        }>
          {filteredFolders.map((folder) => (
            <Card 
              key={folder.id}
              className="hover:shadow-md transition-all duration-200 cursor-pointer hover:scale-[1.02] group"
              onClick={() => handleFolderClick(folder.id)}
              data-testid={`folder-card-${folder.id}`}
            >
              <CardHeader className="pb-3">
                <div className="flex items-start justify-between">
                  <div className="flex items-center gap-3">
                    <div className="relative">
                      {folder.hasSecurityCode ? (
                        <div className="relative">
                          <Folder className="w-8 h-8 text-zeolf-primary group-hover:hidden" />
                          <FolderOpen className="w-8 h-8 text-zeolf-primary hidden group-hover:block" />
                          <Lock className="w-3 h-3 absolute -top-1 -right-1 text-yellow-500 bg-white rounded-full p-0.5" />
                        </div>
                      ) : (
                        <div className="relative">
                          <Folder className="w-8 h-8 text-zeolf-primary group-hover:hidden" />
                          <FolderOpen className="w-8 h-8 text-zeolf-primary hidden group-hover:block" />
                          <Unlock className="w-3 h-3 absolute -top-1 -right-1 text-green-500 bg-white rounded-full p-0.5" />
                        </div>
                      )}
                    </div>
                    <div className="flex-1">
                      <CardTitle className="text-sm font-medium line-clamp-1">
                        {folder.name}
                      </CardTitle>
                      {folder.description && (
                        <p className="text-xs text-gray-500 line-clamp-2 mt-1">
                          {folder.description}
                        </p>
                      )}
                    </div>
                  </div>
                  {folder.hasSecurityCode && (
                    <Badge variant="outline" className="text-xs">
                      Protected
                    </Badge>
                  )}
                </div>
              </CardHeader>
              <CardContent className="pt-0">
                <div className="flex items-center justify-between text-xs text-gray-500">
                  <div className="flex items-center gap-1">
                    <Clock className="w-3 h-3" />
                    <span>{new Date(folder.createdAt).toLocaleDateString()}</span>
                  </div>
                  <div className="flex items-center gap-1">
                    <Users className="w-3 h-3" />
                    <span>Private</span>
                  </div>
                </div>

                {/* Hover Actions */}
                <div className="opacity-0 group-hover:opacity-100 transition-opacity duration-200 mt-3">
                  <Button 
                    size="sm" 
                    className="w-full bg-zeolf-primary hover:bg-zeolf-primary-dark"
                    onClick={(e) => {
                      e.stopPropagation();
                      handleFolderClick(folder.id);
                    }}
                  >
                    Open Folder
                  </Button>
                </div>
              </CardContent>
            </Card>
          ))}
        </div>
      )}

      {/* Quick Stats */}
      {filteredFolders.length > 0 && (
        <div className="flex items-center justify-center gap-6 pt-6 text-sm text-gray-500 border-t">
          <span>{filteredFolders.length} folders</span>
          <span>â€¢</span>
          <span>{filteredFolders.filter(f => f.hasSecurityCode).length} protected</span>
          <span>â€¢</span>
          <span>{filteredFolders.filter(f => !f.hasSecurityCode).length} open access</span>
        </div>
      )}

      {/* Folder Creator Modal */}
      <FolderCreator 
        isOpen={showFolderCreator}
        onClose={() => setShowFolderCreator(false)}
        onSuccess={() => setShowFolderCreator(false)}
      />
    </div>
  );
}

client/src/components/file-uploader.tsx.content:
import { useState } from "react";
import { useMutation, useQueryClient } from "@tanstack/react-query";
import { Upload, FileText, X, Loader2 } from "lucide-react";

import { Button } from "@/components/ui/button";
import {
  Dialog,
  DialogContent,
  DialogDescription,
  DialogHeader,
  DialogTitle,
} from "@/components/ui/dialog";
import {
  Select,
  SelectContent,
  SelectItem,
  SelectTrigger,
  SelectValue,
} from "@/components/ui/select";
import { Label } from "@/components/ui/label";
import { Textarea } from "@/components/ui/textarea";
import { Progress } from "@/components/ui/progress";
import { useToast } from "@/hooks/use-toast";

interface FileUploaderProps {
  open: boolean;
  onClose: () => void;
  folderId: string;
  onSuccess?: () => void;
}

export function FileUploader({ open, onClose, folderId, onSuccess }: FileUploaderProps) {
  const { toast } = useToast();
  const queryClient = useQueryClient();

  const [selectedFile, setSelectedFile] = useState<File | null>(null);
  const [category, setCategory] = useState("memos");
  const [description, setDescription] = useState("");
  const [uploadProgress, setUploadProgress] = useState(0);

  const uploadMutation = useMutation({
    mutationFn: async () => {
      if (!selectedFile) throw new Error("No file selected");

      const formData = new FormData();
      formData.append("file", selectedFile);
      formData.append("category", category);
      formData.append("description", description);
      formData.append("folderId", folderId);

      // Simulate upload progress
      const progressInterval = setInterval(() => {
        setUploadProgress(prev => {
          if (prev >= 90) {
            clearInterval(progressInterval);
            return prev;
          }
          return prev + Math.random() * 20;
        });
      }, 200);

      const response = await fetch("/api/documents/upload", {
        method: "POST",
        body: formData,
        credentials: "include",
      });

      clearInterval(progressInterval);
      setUploadProgress(100);

      if (!response.ok) {
        const errorData = await response.json();
        throw new Error(errorData.message || "Upload failed");
      }

      return await response.json();
    },
    onSuccess: () => {
      toast({
        title: "File uploaded successfully",
        description: "Your file has been added to the folder",
      });

      // Invalidate and refetch folder documents
      queryClient.invalidateQueries({ queryKey: ["/api/folders", folderId, "documents"] });

      // Reset form
      setSelectedFile(null);
      setCategory("memos");
      setDescription("");
      setUploadProgress(0);

      onSuccess?.();
      onClose();
    },
    onError: (error: any) => {
      toast({
        title: "Upload failed",
        description: error.message || "Could not upload file. Please try again.",
        variant: "destructive",
      });
      setUploadProgress(0);
    },
  });

  const handleFileSelect = (event: React.ChangeEvent<HTMLInputElement>) => {
    const file = event.target.files?.[0];
    if (file) {
      // Check file type
      const allowedTypes = [
        'application/pdf',
        'application/msword',
        'application/vnd.openxmlformats-officedocument.wordprocessingml.document',
        'application/vnd.ms-excel',
        'application/vnd.openxmlformats-officedocument.spreadsheetml.sheet',
        'application/vnd.ms-powerpoint',
        'application/vnd.openxmlformats-officedocument.presentationml.presentation'
      ];

      if (!allowedTypes.includes(file.type)) {
        toast({
          title: "Invalid file type",
          description: "Please select a PDF, Word, Excel, or PowerPoint file",
          variant: "destructive",
        });
        return;
      }

      // Check file size (50MB limit)
      if (file.size > 50 * 1024 * 1024) {
        toast({
          title: "File too large",
          description: "Please select a file smaller than 50MB",
          variant: "destructive",
        });
        return;
      }

      setSelectedFile(file);
    }
  };

  const handleUpload = () => {
    if (!selectedFile) {
      toast({
        title: "No file selected",
        description: "Please select a file to upload",
        variant: "destructive",
      });
      return;
    }
    uploadMutation.mutate();
  };

  const handleClose = () => {
    if (!uploadMutation.isPending) {
      setSelectedFile(null);
      setCategory("memos");
      setDescription("");
      setUploadProgress(0);
      onClose();
    }
  };

  return (
    <Dialog open={open} onOpenChange={handleClose}>
      <DialogContent className="sm:max-w-md">
        <DialogHeader>
          <DialogTitle className="flex items-center gap-2">
            <Upload className="w-5 h-5 text-zeolf-blue" />
            Upload File to Folder
          </DialogTitle>
          <DialogDescription>
            Upload an existing document file to this folder. Supported formats: PDF, Word, Excel, PowerPoint.
          </DialogDescription>
        </DialogHeader>

        <div className="space-y-6">
          {/* File Selection */}
          <div className="space-y-2">
            <Label>Select File</Label>
            <div 
              className={`border-2 border-dashed rounded-lg p-6 text-center transition-colors ${
                selectedFile 
                  ? 'border-zeolf-blue bg-zeolf-blue/5' 
                  : 'border-gray-300 hover:border-zeolf-blue hover:bg-gray-50'
              }`}
            >
              {selectedFile ? (
                <div className="flex items-center justify-between">
                  <div className="flex items-center gap-3">
                    <FileText className="w-8 h-8 text-zeolf-blue" />
                    <div className="text-left">
                      <p className="font-medium text-gray-900">{selectedFile.name}</p>
                      <p className="text-sm text-gray-500">
                        {(selectedFile.size / 1024 / 1024).toFixed(2)} MB
                      </p>
                    </div>
                  </div>
                  <Button
                    variant="ghost"
                    size="sm"
                    onClick={() => setSelectedFile(null)}
                    disabled={uploadMutation.isPending}
                    data-testid="button-remove-file"
                  >
                    <X className="w-4 h-4" />
                  </Button>
                </div>
              ) : (
                <div>
                  <Upload className="w-12 h-12 mx-auto text-gray-400 mb-4" />
                  <p className="text-gray-600 mb-2">Click to select a file</p>
                  <p className="text-sm text-gray-500">
                    PDF, Word, Excel, or PowerPoint (max 50MB)
                  </p>
                  <input
                    type="file"
                    onChange={handleFileSelect}
                    accept=".pdf,.doc,.docx,.xls,.xlsx,.ppt,.pptx"
                    className="absolute inset-0 w-full h-full opacity-0 cursor-pointer"
                    disabled={uploadMutation.isPending}
                    data-testid="input-file-upload"
                  />
                </div>
              )}
            </div>
            {!selectedFile && (
              <input
                type="file"
                onChange={handleFileSelect}
                accept=".pdf,.doc,.docx,.xls,.xlsx,.ppt,.pptx"
                className="hidden"
                disabled={uploadMutation.isPending}
                id="file-input"
              />
            )}
          </div>

          {/* Category Selection */}
          <div className="space-y-2">
            <Label>Document Category</Label>
            <Select value={category} onValueChange={setCategory} disabled={uploadMutation.isPending}>
              <SelectTrigger data-testid="select-file-category">
                <SelectValue />
              </SelectTrigger>
              <SelectContent>
                <SelectItem value="press_releases">Press Releases</SelectItem>
                <SelectItem value="memos">Memos</SelectItem>
                <SelectItem value="internal_letters">Internal Letters</SelectItem>
                <SelectItem value="contracts">Contracts</SelectItem>
                <SelectItem value="follow_ups">Follow-ups</SelectItem>
              </SelectContent>
            </Select>
          </div>

          {/* Description */}
          <div className="space-y-2">
            <Label>Description (Optional)</Label>
            <Textarea
              placeholder="Add a description for this document..."
              value={description}
              onChange={(e) => setDescription(e.target.value)}
              rows={3}
              disabled={uploadMutation.isPending}
              data-testid="input-file-description"
            />
          </div>

          {/* Upload Progress */}
          {uploadMutation.isPending && (
            <div className="space-y-2">
              <div className="flex items-center justify-between text-sm">
                <span>Uploading...</span>
                <span>{Math.round(uploadProgress)}%</span>
              </div>
              <Progress value={uploadProgress} className="w-full" />
            </div>
          )}

          {/* Action Buttons */}
          <div className="flex items-center justify-end gap-3 pt-4 border-t">
            <Button
              variant="outline"
              onClick={handleClose}
              disabled={uploadMutation.isPending}
              data-testid="button-cancel-upload"
            >
              Cancel
            </Button>
            <Button
              onClick={handleUpload}
              disabled={!selectedFile || uploadMutation.isPending}
              className="bg-zeolf-blue hover:bg-zeolf-blue-dark"
              data-testid="button-confirm-upload"
            >
              {uploadMutation.isPending ? (
                <>
                  <Loader2 className="w-4 h-4 mr-2 animate-spin" />
                  Uploading...
                </>
              ) : (
                <>
                  <Upload className="w-4 h-4 mr-2" />
                  Upload File
                </>
              )}
            </Button>
          </div>
        </div>
      </DialogContent>
    </Dialog>
  );
}

