import { useState } from "react";
import { useMutation } from "@tanstack/react-query";
import { Bot, Lightbulb, Search, FileText, Loader2 } from "lucide-react";

import { Button } from "@/components/ui/button";
import {
  Dialog,
  DialogContent,
  DialogDescription,
  DialogHeader,
  DialogTitle,
  DialogTrigger,
} from "@/components/ui/dialog";
import { Tabs, TabsContent, TabsList, TabsTrigger } from "@/components/ui/tabs";
import { Textarea } from "@/components/ui/textarea";
import { Label } from "@/components/ui/label";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import {
  Select,
  SelectContent,
  SelectItem,
  SelectTrigger,
  SelectValue,
} from "@/components/ui/select";
import { useToast } from "@/hooks/use-toast";
import { apiRequest } from "@/lib/queryClient";

interface GrokAssistantProps {
  open?: boolean;
  onClose?: () => void;
  onTemplateGenerated?: (template: string) => void;
  onResearchCompleted?: (research: string) => void;
  documentType?: string;
  currentContent?: string;
}

export function GrokAssistant({ 
  open,
  onClose,
  onTemplateGenerated, 
  onResearchCompleted,
  documentType = "",
  currentContent = ""
}: GrokAssistantProps) {
  const [isOpen, setIsOpen] = useState(false);
  const [activeTab, setActiveTab] = useState("template");
  const { toast } = useToast();

  const dialogOpen = open !== undefined ? open : isOpen;
  const handleOpenChange = onClose || ((open: boolean) => setIsOpen(open));

  // Template Generation
  const [templateForm, setTemplateForm] = useState({
    documentType: documentType || "memo",
    title: "",
    fileType: "word",
    recipientName: "",
    recipientAddress: "",
    recipientTitle: "",
    isInternal: true,
  });

  const templateMutation = useMutation({
    mutationFn: async (data: any) => {
      const response = await apiRequest("POST", "/api/ai/generate-template", data);
      return await response.json();
    },
    onSuccess: (data) => {
      onTemplateGenerated?.(data.template);
      toast({
        title: "Template generated",
        description: "Your document template has been created successfully.",
      });
    },
    onError: (error: any) => {
      toast({
        title: "Error generating template",
        description: error.message || "Please try again.",
        variant: "destructive",
      });
    },
  });

  // Research
  const [researchForm, setResearchForm] = useState({
    topic: "",
    documentType: documentType || "memo",
    context: "",
  });

  const researchMutation = useMutation({
    mutationFn: async (data: any) => {
      const response = await apiRequest("POST", "/api/ai/research", data);
      return await response.json();
    },
    onSuccess: (data) => {
      onResearchCompleted?.(data.research);
      toast({
        title: "Research completed",
        description: "AI research has been completed successfully.",
      });
    },
    onError: (error: any) => {
      toast({
        title: "Error performing research",
        description: error.message || "Please try again.",
        variant: "destructive",
      });
    },
  });

  // Content Improvement
  const improveMutation = useMutation({
    mutationFn: async (data: any) => {
      const response = await apiRequest("POST", "/api/ai/improve-content", data);
      return await response.json();
    },
    onSuccess: (data) => {
      onTemplateGenerated?.(data.improvedContent);
      toast({
        title: "Content improved",
        description: "Your document content has been enhanced.",
      });
    },
    onError: (error: any) => {
      toast({
        title: "Error improving content",
        description: error.message || "Please try again.",
        variant: "destructive",
      });
    },
  });

  return (
    <Dialog open={dialogOpen} onOpenChange={handleOpenChange}>
      <DialogTrigger asChild>
        <Button 
          variant="outline" 
          size="sm"
          className="gap-2"
          data-testid="button-grok-assistant"
        >
          <Bot className="w-4 h-4" />
          AI Assistant
        </Button>
      </DialogTrigger>
      <DialogContent className="sm:max-w-2xl max-h-[80vh] overflow-y-auto">
        <DialogHeader>
          <DialogTitle className="flex items-center gap-2">
            <Bot className="w-5 h-5 text-zeolf-primary" />
            Grok AI Assistant
          </DialogTitle>
          <DialogDescription>
            Get AI-powered help with document templates, research, and content improvement.
          </DialogDescription>
        </DialogHeader>

        <Tabs value={activeTab} onValueChange={setActiveTab} className="w-full">
          <TabsList className="grid w-full grid-cols-3">
            <TabsTrigger value="template" data-testid="tab-template">
              <FileText className="w-4 h-4 mr-2" />
              Template
            </TabsTrigger>
            <TabsTrigger value="research" data-testid="tab-research">
              <Search className="w-4 h-4 mr-2" />
              Research
            </TabsTrigger>
            <TabsTrigger value="improve" data-testid="tab-improve">
              <Lightbulb className="w-4 h-4 mr-2" />
              Improve
            </TabsTrigger>
          </TabsList>

          <TabsContent value="template" className="mt-6">
            <Card>
              <CardHeader>
                <CardTitle>Generate Document Template</CardTitle>
                <CardDescription>
                  Create a professional document template based on your specifications.
                </CardDescription>
              </CardHeader>
              <CardContent className="space-y-4">
                <div className="grid grid-cols-2 gap-4">
                  <div>
                    <Label>Document Type</Label>
                    <Select 
                      value={templateForm.documentType}
                      onValueChange={(value) => setTemplateForm(prev => ({...prev, documentType: value}))}
                    >
                      <SelectTrigger data-testid="select-template-type">
                        <SelectValue />
                      </SelectTrigger>
                      <SelectContent>
                        <SelectItem value="press_release">Press Release</SelectItem>
                        <SelectItem value="memo">Memo</SelectItem>
                        <SelectItem value="internal_letter">Internal Letter</SelectItem>
                        <SelectItem value="external_letter">External Letter</SelectItem>
                        <SelectItem value="contract">Contract</SelectItem>
                        <SelectItem value="follow_up">Follow-up</SelectItem>
                        <SelectItem value="report">Report</SelectItem>
                      </SelectContent>
                    </Select>
                  </div>

                  <div>
                    <Label>File Type</Label>
                    <Select 
                      value={templateForm.fileType}
                      onValueChange={(value) => setTemplateForm(prev => ({...prev, fileType: value}))}
                    >
                      <SelectTrigger data-testid="select-template-filetype">
                        <SelectValue />
                      </SelectTrigger>
                      <SelectContent>
                        <SelectItem value="word">Word Document</SelectItem>
                        <SelectItem value="excel">Excel Spreadsheet</SelectItem>
                        <SelectItem value="powerpoint">PowerPoint Presentation</SelectItem>
                      </SelectContent>
                    </Select>
                  </div>
                </div>

                <div>
                  <Label>Document Title</Label>
                  <Textarea
                    placeholder="Enter the document title or subject"
                    value={templateForm.title}
                    onChange={(e) => setTemplateForm(prev => ({...prev, title: e.target.value}))}
                    rows={2}
                    data-testid="input-template-title"
                  />
                </div>

                {(templateForm.documentType.includes("letter") || templateForm.documentType === "external_letter") && (
                  <div className="space-y-4 border rounded-lg p-4 bg-gray-50">
                    <h4 className="font-medium">Recipient Information</h4>
                    <div className="grid grid-cols-2 gap-4">
                      <div>
                        <Label>Recipient Name</Label>
                        <input
                          className="w-full p-2 border rounded"
                          placeholder="Full name"
                          value={templateForm.recipientName}
                          onChange={(e) => setTemplateForm(prev => ({...prev, recipientName: e.target.value}))}
                          data-testid="input-recipient-name"
                        />
                      </div>
                      <div>
                        <Label>Title</Label>
                        <input
                          className="w-full p-2 border rounded"
                          placeholder="Job title"
                          value={templateForm.recipientTitle}
                          onChange={(e) => setTemplateForm(prev => ({...prev, recipientTitle: e.target.value}))}
                          data-testid="input-recipient-title"
                        />
                      </div>
                    </div>
                    <div>
                      <Label>Address</Label>
                      <Textarea
                        placeholder="Full address"
                        value={templateForm.recipientAddress}
                        onChange={(e) => setTemplateForm(prev => ({...prev, recipientAddress: e.target.value}))}
                        rows={3}
                        data-testid="input-recipient-address"
                      />
                    </div>
                  </div>
                )}

                <Button
                  onClick={() => templateMutation.mutate(templateForm)}
                  disabled={templateMutation.isPending || !templateForm.title}
                  className="w-full bg-zeolf-primary hover:bg-zeolf-primary-dark"
                  data-testid="button-generate-template"
                >
                  {templateMutation.isPending ? (
                    <>
                      <Loader2 className="w-4 h-4 mr-2 animate-spin" />
                      Generating Template...
                    </>
                  ) : (
                    "Generate Template"
                  )}
                </Button>
              </CardContent>
            </Card>
          </TabsContent>

          <TabsContent value="research" className="mt-6">
            <Card>
              <CardHeader>
                <CardTitle>AI Research Assistant</CardTitle>
                <CardDescription>
                  Get comprehensive research information for your document.
                </CardDescription>
              </CardHeader>
              <CardContent className="space-y-4">
                <div>
                  <Label>Research Topic</Label>
                  <Textarea
                    placeholder="What do you want to research? Be specific about the topic or question."
                    value={researchForm.topic}
                    onChange={(e) => setResearchForm(prev => ({...prev, topic: e.target.value}))}
                    rows={3}
                    data-testid="input-research-topic"
                  />
                </div>

                <div>
                  <Label>Document Type</Label>
                  <Select 
                    value={researchForm.documentType}
                    onValueChange={(value) => setResearchForm(prev => ({...prev, documentType: value}))}
                  >
                    <SelectTrigger data-testid="select-research-type">
                      <SelectValue />
                    </SelectTrigger>
                    <SelectContent>
                      <SelectItem value="press_release">Press Release</SelectItem>
                      <SelectItem value="memo">Memo</SelectItem>
                      <SelectItem value="internal_letter">Internal Letter</SelectItem>
                      <SelectItem value="external_letter">External Letter</SelectItem>
                      <SelectItem value="contract">Contract</SelectItem>
                      <SelectItem value="follow_up">Follow-up</SelectItem>
                      <SelectItem value="report">Report</SelectItem>
                    </SelectContent>
                  </Select>
                </div>

                <div>
                  <Label>Additional Context (Optional)</Label>
                  <Textarea
                    placeholder="Provide any additional context or specific requirements for the research"
                    value={researchForm.context}
                    onChange={(e) => setResearchForm(prev => ({...prev, context: e.target.value}))}
                    rows={3}
                    data-testid="input-research-context"
                  />
                </div>

                <Button
                  onClick={() => researchMutation.mutate(researchForm)}
                  disabled={researchMutation.isPending || !researchForm.topic}
                  className="w-full bg-zeolf-primary hover:bg-zeolf-primary-dark"
                  data-testid="button-start-research"
                >
                  {researchMutation.isPending ? (
                    <>
                      <Loader2 className="w-4 h-4 mr-2 animate-spin" />
                      Researching...
                    </>
                  ) : (
                    "Start Research"
                  )}
                </Button>
              </CardContent>
            </Card>
          </TabsContent>

          <TabsContent value="improve" className="mt-6">
            <Card>
              <CardHeader>
                <CardTitle>Improve Document Content</CardTitle>
                <CardDescription>
                  Enhance your existing document with AI-powered improvements.
                </CardDescription>
              </CardHeader>
              <CardContent className="space-y-4">
                <div>
                  <Label>Current Content</Label>
                  <Textarea
                    placeholder="Paste your document content here to improve it"
                    value={currentContent}
                    rows={8}
                    data-testid="input-improve-content"
                    readOnly={!!currentContent}
                  />
                </div>

                <Button
                  onClick={() => improveMutation.mutate({ 
                    content: currentContent, 
                    documentType: documentType || "memo" 
                  })}
                  disabled={improveMutation.isPending || !currentContent}
                  className="w-full bg-zeolf-primary hover:bg-zeolf-primary-dark"
                  data-testid="button-improve-content"
                >
                  {improveMutation.isPending ? (
                    <>
                      <Loader2 className="w-4 h-4 mr-2 animate-spin" />
                      Improving Content...
                    </>
                  ) : (
                    "Improve Content"
                  )}
                </Button>
              </CardContent>
            </Card>
          </TabsContent>
        </Tabs>
      </DialogContent>
    </Dialog>
  );
}

client/src/components/upload-modal.tsx.content:
import { useState } from "react";
import { useMutation, useQueryClient } from "@tanstack/react-query";
import {
  Dialog,
  DialogContent,
  DialogDescription,
  DialogHeader,
  DialogTitle,
} from "@/components/ui/dialog";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Checkbox } from "@/components/ui/checkbox";
import { CloudUpload, X, Upload, Loader2 } from "lucide-react";
import { useToast } from "@/hooks/use-toast";

interface UploadModalProps {
  open: boolean;
  onOpenChange: (open: boolean) => void;
}

const categories = [
  { value: "press_releases", label: "Press Releases" },
  { value: "memos", label: "Memos" },
  { value: "internal_letters", label: "Internal Letters" },
  { value: "contracts", label: "Contracts" },
  { value: "follow_ups", label: "Follow-ups" },
];

export function UploadModal({ open, onOpenChange }: UploadModalProps) {
  const [file, setFile] = useState<File | null>(null);
  const [category, setCategory] = useState("");
  const [customName, setCustomName] = useState("");
  const [addHeader, setAddHeader] = useState(true);
  const [dragActive, setDragActive] = useState(false);

  const { toast } = useToast();
  const queryClient = useQueryClient();

  const uploadMutation = useMutation({
    mutationFn: async (formData: FormData) => {
      const response = await fetch('/api/documents/upload', {
        method: 'POST',
        body: formData,
        credentials: 'include',
      });

      if (!response.ok) {
        const error = await response.json();
        throw new Error(error.message || 'Upload failed');
      }

      return response.json();
    },
    onSuccess: () => {
      toast({
        title: "Upload successful",
        description: "Document has been uploaded successfully",
      });

      // Reset form
      setFile(null);
      setCategory("");
      setCustomName("");
      setAddHeader(true);

      // Close modal
      onOpenChange(false);

      // Invalidate queries to refresh document list
      queryClient.invalidateQueries({ queryKey: ["/api/documents"] });
      queryClient.invalidateQueries({ queryKey: ["/api/documents/stats"] });
    },
    onError: (error: Error) => {
      toast({
        title: "Upload failed",
        description: error.message,
        variant: "destructive",
      });
    },
  });

  const handleFileSelect = (selectedFile: File | null) => {
    if (selectedFile) {
      // Validate file type
      const allowedTypes = [
        'application/pdf',
        'application/msword',
        'application/vnd.openxmlformats-officedocument.wordprocessingml.document',
        'application/vnd.ms-excel',
        'application/vnd.openxmlformats-officedocument.spreadsheetml.sheet',
        'application/vnd.ms-powerpoint',
        'application/vnd.openxmlformats-officedocument.presentationml.presentation',
      ];

      if (!allowedTypes.includes(selectedFile.type)) {
        toast({
          title: "Invalid file type",
          description: "Only PDF, Word, Excel, and PowerPoint files are allowed",
          variant: "destructive",
        });
        return;
      }

      // Validate file size (50MB limit)
      if (selectedFile.size > 50 * 1024 * 1024) {
        toast({
          title: "File too large",
          description: "File size must be less than 50MB",
          variant: "destructive",
        });
        return;
      }

      setFile(selectedFile);
      if (!customName) {
        setCustomName(selectedFile.name.replace(/\.[^/.]+$/, "")); // Remove extension
      }
    }
  };

  const handleDrag = (e: React.DragEvent) => {
    e.preventDefault();
    e.stopPropagation();
    if (e.type === "dragenter" || e.type === "dragover") {
      setDragActive(true);
    } else if (e.type === "dragleave") {
      setDragActive(false);
    }
  };

  const handleDrop = (e: React.DragEvent) => {
    e.preventDefault();
    e.stopPropagation();
    setDragActive(false);

    if (e.dataTransfer.files && e.dataTransfer.files[0]) {
      handleFileSelect(e.dataTransfer.files[0]);
    }
  };

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();

    if (!file || !category) {
      toast({
        title: "Missing information",
        description: "Please select a file and category",
        variant: "destructive",
      });
      return;
    }

    const formData = new FormData();
    formData.append('file', file);
    formData.append('category', category);
    if (customName) {
      formData.append('name', customName);
    }
    formData.append('addHeader', addHeader.toString());

    uploadMutation.mutate(formData);
  };

  const handleClose = () => {
    if (!uploadMutation.isPending) {
      setFile(null);
      setCategory("");
      setCustomName("");
      setAddHeader(true);
      onOpenChange(false);
    }
  };

  return (
    <Dialog open={open} onOpenChange={handleClose}>
      <DialogContent className="sm:max-w-lg" data-testid="upload-modal">
        <DialogHeader>
          <DialogTitle className="flex items-center justify-between">
            Upload Document
            <Button
              variant="ghost"
              size="sm"
              onClick={handleClose}
              disabled={uploadMutation.isPending}
              data-testid="button-close-upload"
            >
              <X className="w-4 h-4" />
            </Button>
          </DialogTitle>
          <DialogDescription>
            Upload a file to add it to your document collection. Supported formats: PDF, Word, Excel, PowerPoint.
          </DialogDescription>
        </DialogHeader>

        <form onSubmit={handleSubmit} className="space-y-6">
          {/* Upload Area */}
          <div
            className={`border-2 border-dashed rounded-lg p-8 text-center transition-colors ${
              dragActive
                ? "border-zeolf-blue bg-zeolf-blue/5"
                : "border-gray-300 hover:border-zeolf-blue"
            }`}
            onDragEnter={handleDrag}
            onDragLeave={handleDrag}
            onDragOver={handleDrag}
            onDrop={handleDrop}
            data-testid="upload-drop-zone"
          >
            <CloudUpload className="w-12 h-12 text-zeolf-text-secondary mx-auto mb-4" />

            {file ? (
              <div className="space-y-2">
                <p className="text-zeolf-text font-medium">{file.name}</p>
                <p className="text-sm text-zeolf-text-secondary">
                  {(file.size / 1024 / 1024).toFixed(2)} MB
                </p>
                <Button
                  type="button"
                  variant="outline"
                  size="sm"
                  onClick={() => setFile(null)}
                  data-testid="button-remove-file"
                >
                  Remove
                </Button>
              </div>
            ) : (
              <div className="space-y-4">
                <p className="text-zeolf-text mb-2">Drag and drop files here</p>
                <p className="text-sm text-zeolf-text-secondary mb-4">or</p>
                <Button
                  type="button"
                  className="bg-zeolf-blue hover:bg-zeolf-blue-dark"
                  onClick={() => document.getElementById('file-input')?.click()}
                  data-testid="button-browse-files"
                >
                  Browse Files
                </Button>
                <p className="text-xs text-zeolf-text-secondary">
                  Supports PDF, Word, Excel, PowerPoint (max 50MB)
                </p>
              </div>
            )}

            <input
              id="file-input"
              type="file"
              className="hidden"
              accept=".pdf,.doc,.docx,.xls,.xlsx,.ppt,.pptx"
              onChange={(e) => handleFileSelect(e.target.files?.[0] || null)}
              data-testid="input-file"
            />
          </div>

          {/* Document Details */}
          <div className="space-y-4">
            <div>
              <Label htmlFor="category" className="text-zeolf-text">Category *</Label>
              <Select value={category} onValueChange={setCategory} required>
                <SelectTrigger data-testid="select-category">
                  <SelectValue placeholder="Select category..." />
                </SelectTrigger>
                <SelectContent>
                  {categories.map((cat) => (
                    <SelectItem key={cat.value} value={cat.value}>
                      {cat.label}
                    </SelectItem>
                  ))}
                </SelectContent>
              </Select>
            </div>

            <div>
              <Label htmlFor="customName" className="text-zeolf-text">Document Name</Label>
              <Input
                id="customName"
                value={customName}
                onChange={(e) => setCustomName(e.target.value)}
                placeholder="Auto-generated based on content"
                data-testid="input-document-name"
              />
            </div>

            <div className="flex items-center space-x-2">
              <Checkbox
                id="autoHeader"
                checked={addHeader}
                onCheckedChange={(checked) => setAddHeader(checked === true)}
                data-testid="checkbox-auto-header"
              />
              <Label htmlFor="autoHeader" className="text-sm text-zeolf-text">
                Add ZEOLF company header automatically
              </Label>
            </div>
          </div>

          {/* Submit Button */}
          <div className="flex justify-end space-x-3">
            <Button
              type="button"
              variant="outline"
              onClick={handleClose}
              disabled={uploadMutation.isPending}
              data-testid="button-cancel-upload"
            >
              Cancel
            </Button>
            <Button
              type="submit"
              className="bg-zeolf-blue hover:bg-zeolf-blue-dark"
              disabled={!file || !category || uploadMutation.isPending}
              data-testid="button-submit-upload"
            >
              {uploadMutation.isPending ? (
                <>
                  <Loader2 className="w-4 h-4 mr-2 animate-spin" />
                  Uploading...
                </>
              ) : (
                <>
                  <Upload className="w-4 h-4 mr-2" />
                  Upload
                </>
              )}
            </Button>
          </div>
        </form>
      </DialogContent>
    </Dialog>
  );
}

client/src/components/video-conference.t.content:
import { useState, useEffect } from "react";
import {
  Dialog,
  DialogContent,
  DialogDescription,
  DialogHeader,
  DialogTitle,
} from "@/components/ui/dialog";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { 
  Mic, 
  MicOff, 
  Video as VideoIcon, 
  VideoOff, 
  Monitor, 
  MessageSquare, 
  Phone,
  PhoneOff,
  X,
  Users,
  Copy,
  Shield
} from "lucide-react";
import { useToast } from "@/hooks/use-toast";

interface VideoConferenceProps {
  open: boolean;
  onOpenChange: (open: boolean) => void;
}

interface Participant {
  id: string;
  name: string;
  initials: string;
  isHost: boolean;
  micEnabled: boolean;
  videoEnabled: boolean;
  color: string;
}

export function VideoConference({ open, onOpenChange }: VideoConferenceProps) {
  const [duration, setDuration] = useState(0);
  const [isRecording] = useState(true);
  const [localMicEnabled, setLocalMicEnabled] = useState(true);
  const [localVideoEnabled, setLocalVideoEnabled] = useState(true);
  const [isScreenSharing, setIsScreenSharing] = useState(false);
  const { toast } = useToast();

  // Mock participants data
  const [participants] = useState<Participant[]>([
    { id: '1', name: 'John Doe', initials: 'JD', isHost: true, micEnabled: true, videoEnabled: true, color: 'bg-zeolf-blue' },
    { id: '2', name: 'Sarah Miller', initials: 'SM', isHost: false, micEnabled: true, videoEnabled: false, color: 'bg-zeolf-accent' },
    { id: '3', name: 'Robert Johnson', initials: 'RJ', isHost: false, micEnabled: false, videoEnabled: true, color: 'bg-zeolf-success' },
    { id: '4', name: 'Lisa Wang', initials: 'LW', isHost: false, micEnabled: true, videoEnabled: true, color: 'bg-zeolf-warning' },
  ]);

  const meetingId = "123-456-789";

  // Timer effect
  useEffect(() => {
    let interval: NodeJS.Timeout;
    if (open) {
      interval = setInterval(() => {
        setDuration(prev => prev + 1);
      }, 1000);
    }
    return () => clearInterval(interval);
  }, [open]);

  // Reset timer when modal closes
  useEffect(() => {
    if (!open) {
      setDuration(0);
    }
  }, [open]);

  const formatDuration = (seconds: number) => {
    const hours = Math.floor(seconds / 3600);
    const minutes = Math.floor((seconds % 3600) / 60);
    const secs = seconds % 60;
    return `${hours.toString().padStart(2, '0')}:${minutes.toString().padStart(2, '0')}:${secs.toString().padStart(2, '0')}`;
  };

  const handleToggleMic = () => {
    setLocalMicEnabled(!localMicEnabled);
    toast({
      title: localMicEnabled ? "Microphone muted" : "Microphone unmuted",
    });
  };

  const handleToggleVideo = () => {
    setLocalVideoEnabled(!localVideoEnabled);
    toast({
      title: localVideoEnabled ? "Camera turned off" : "Camera turned on",
    });
  };

  const handleToggleScreenShare = () => {
    setIsScreenSharing(!isScreenSharing);
    toast({
      title: isScreenSharing ? "Screen sharing stopped" : "Screen sharing started",
    });
  };

  const handleCopyMeetingLink = () => {
    const meetingLink = `https://zeolf-dms.com/meeting/${meetingId}`;
    navigator.clipboard.writeText(meetingLink);
    toast({
      title: "Meeting link copied",
      description: "Share this link with other participants",
    });
  };

  const handleEndCall = () => {
    onOpenChange(false);
    toast({
      title: "Meeting ended",
      description: "You have left the meeting",
    });
  };

  if (!open) return null;

  return (
    <Dialog open={open} onOpenChange={onOpenChange}>
      <DialogContent className="max-w-full max-h-full w-screen h-screen p-0 bg-black">
        <DialogHeader className="sr-only">
          <DialogTitle>ZEOLF Team Video Conference</DialogTitle>
          <DialogDescription>
            Full-screen video conference meeting with screen sharing, chat, and recording capabilities.
          </DialogDescription>
        </DialogHeader>
        <div className="flex flex-col h-full" data-testid="video-conference">
          {/* Header */}
          <div className="flex items-center justify-between p-4 bg-black/50 text-white">
            <div className="flex items-center space-x-4">
              <h3 className="font-medium">ZEOLF Team Meeting</h3>
              <span className="text-sm text-gray-300">{formatDuration(duration)}</span>
              {isRecording && (
                <div className="flex items-center space-x-2">
                  <div className="w-2 h-2 bg-red-500 rounded-full animate-pulse"></div>
                  <span className="text-sm">Recording</span>
                </div>
              )}
            </div>

            <div className="flex items-center space-x-3">
              <Button
                variant="ghost"
                size="sm"
                className="text-white hover:text-gray-300"
                data-testid="button-participants"
              >
                <Users className="w-4 h-4 mr-2" />
                <span>{participants.length} participants</span>
              </Button>
              <Button
                variant="ghost"
                size="sm"
                onClick={() => onOpenChange(false)}
                className="text-white hover:text-gray-300"
                data-testid="button-close-meeting"
              >
                <X className="w-5 h-5" />
              </Button>
            </div>
          </div>

          {/* Video Grid */}
          <div className="flex-1 p-4">
            <div className="grid grid-cols-2 lg:grid-cols-2 gap-4 h-full">
              {participants.map((participant, index) => (
                <div 
                  key={participant.id} 
                  className="relative bg-gray-900 rounded-lg overflow-hidden"
                  data-testid={`participant-${participant.id}`}
                >
                  <div className="absolute inset-0 flex items-center justify-center">
                    <div className="text-center text-white">
                      <div className={`w-${index === 0 ? '20' : '16'} h-${index === 0 ? '20' : '16'} ${participant.color} rounded-full flex items-center justify-center mx-auto mb-${index === 0 ? '4' : '3'}`}>
                        <span className={`${index === 0 ? 'text-2xl' : 'text-lg'} font-medium`}>
                          {participant.initials}
                        </span>
                      </div>
                      <p className="font-medium">{participant.name}</p>
                      {participant.isHost && (
                        <Badge variant="secondary" className="text-xs mt-1">
                          Host
                        </Badge>
                      )}
                    </div>
                  </div>

                  {/* Participant Controls Indicator */}
                  <div className="absolute bottom-4 left-4 flex items-center space-x-2">
                    {participant.micEnabled ? (
                      <Mic className="w-4 h-4 text-white" />
                    ) : (
                      <MicOff className="w-4 h-4 text-red-500" />
                    )}
                    {participant.videoEnabled ? (
                      <VideoIcon className="w-4 h-4 text-white" />
                    ) : (
                      <VideoOff className="w-4 h-4 text-red-500" />
                    )}
                  </div>
                </div>
              ))}
            </div>
          </div>

          {/* Controls */}
          <div className="bg-black/50 p-4">
            <div className="flex items-center justify-center space-x-6 mb-4">
              <Button
                variant={localMicEnabled ? "secondary" : "destructive"}
                size="lg"
                className="w-12 h-12 rounded-full"
                onClick={handleToggleMic}
                data-testid="button-toggle-mic"
              >
                {localMicEnabled ? <Mic className="w-5 h-5" /> : <MicOff className="w-5 h-5" />}
              </Button>

              <Button
                variant={localVideoEnabled ? "secondary" : "destructive"}
                size="lg"
                className="w-12 h-12 rounded-full"
                onClick={handleToggleVideo}
                data-testid="button-toggle-video"
              >
                {localVideoEnabled ? <VideoIcon className="w-5 h-5" /> : <VideoOff className="w-5 h-5" />}
              </Button>

              <Button
                variant={isScreenSharing ? "default" : "secondary"}
                size="lg"
                className="w-12 h-12 rounded-full bg-zeolf-blue hover:bg-zeolf-blue-dark"
                onClick={handleToggleScreenShare}
                data-testid="button-screen-share"
              >
                <Monitor className="w-5 h-5" />
              </Button>

              <Button
                variant="secondary"
                size="lg"
                className="w-12 h-12 rounded-full"
                data-testid="button-chat"
              >
                <MessageSquare className="w-5 h-5" />
              </Button>

              <Button
                variant="destructive"
                size="lg"
                className="w-12 h-12 rounded-full"
                onClick={handleEndCall}
                data-testid="button-end-call"
              >
                <PhoneOff className="w-5 h-5" />
              </Button>
            </div>

            <div className="flex items-center justify-between text-white text-sm">
              <div className="flex items-center space-x-4">
                <span>Meeting ID: {meetingId}</span>
                <Button
                  variant="link"
                  size="sm"
                  className="text-zeolf-accent hover:text-blue-300 p-0 h-auto"
                  onClick={handleCopyMeetingLink}
                  data-testid="button-copy-link"
                >
                  <Copy className="w-3 h-3 mr-1" />
                  Copy Link
                </Button>
              </div>
              <div className="flex items-center space-x-2 text-gray-300">
                <Shield className="w-4 h-4" />
                <span>End-to-end encrypted</span>
              </div>
            </div>
          </div>
        </div>
      </DialogContent>
    </Dialog>
  );
}

client/src/hooks/use-auth.tsx.content:
import { createContext, ReactNode, useContext } from "react";
import {
  useQuery,
  useMutation,
  UseMutationResult,
} from "@tanstack/react-query";
import { insertUserSchema, User as SelectUser, InsertUser } from "@shared/schema";
import { getQueryFn, apiRequest, queryClient } from "../lib/queryClient";
import { useToast } from "@/hooks/use-toast";

type AuthContextType = {
  user: SelectUser | null;
  isLoading: boolean;
  error: Error | null;
  loginMutation: UseMutationResult<SelectUser, Error, LoginData>;
  logoutMutation: UseMutationResult<void, Error, void>;
  registerMutation: UseMutationResult<SelectUser, Error, RegisterData>;
};

type LoginData = {
  loginCode: string;
};

type RegisterData = {
  name: string;
  role?: string;
};

export const AuthContext = createContext<AuthContextType | null>(null);

export function AuthProvider({ children }: { children: ReactNode }) {
  const { toast } = useToast();
  const {
    data: user,
    error,
    isLoading,
  } = useQuery<SelectUser | undefined, Error>({
    queryKey: ["/api/user"],
    queryFn: getQueryFn({ on401: "returnNull" }),
  });

  const loginMutation = useMutation({
    mutationFn: async (credentials: LoginData) => {
      const res = await apiRequest("POST", "/api/login", credentials);
      return await res.json();
    },
    onSuccess: (user: SelectUser) => {
      queryClient.setQueryData(["/api/user"], user);
      toast({
        title: "Login successful",
        description: `Welcome back, ${user.name}!`,
      });
    },
    onError: (error: Error) => {
      toast({
        title: "Login failed",
        description: error.message,
        variant: "destructive",
      });
    },
  });

  const registerMutation = useMutation({
    mutationFn: async (credentials: RegisterData) => {
      const res = await apiRequest("POST", "/api/register", credentials);
      return await res.json();
    },
    onSuccess: (newUser: SelectUser) => {
      queryClient.invalidateQueries({ queryKey: ["/api/admin/users"] });
      toast({
        title: "User created successfully",
        description: `Login code: ${newUser.loginCode}`,
      });
    },
    onError: (error: Error) => {
      toast({
        title: "User creation failed",
        description: error.message,
        variant: "destructive",
      });
    },
  });

  const logoutMutation = useMutation({
    mutationFn: async () => {
      await apiRequest("POST", "/api/logout");
    },
    onSuccess: () => {
      queryClient.setQueryData(["/api/user"], null);
      queryClient.clear();
      toast({
        title: "Logged out",
        description: "You have been successfully logged out.",
      });
    },
    onError: (error: Error) => {
      toast({
        title: "Logout failed",
        description: error.message,
        variant: "destructive",
      });
    },
  });

  return (
    <AuthContext.Provider
      value={{
        user: user ?? null,
        isLoading,
        error,
        loginMutation,
        logoutMutation,
        registerMutation,
      }}
    >
      {children}
    </AuthContext.Provider>
  );
}

export function useAuth() {
  const context = useContext(AuthContext);
  if (!context) {
    throw new Error("useAuth must be used within an AuthProvider");
  }
  return context;
}

client/src/hooks/use-mobile.tsx.content:
import * as React from "react"

const MOBILE_BREAKPOINT = 768

export function useIsMobile() {
  const [isMobile, setIsMobile] = React.useState<boolean | undefined>(undefined)

  React.useEffect(() => {
    const mql = window.matchMedia(`(max-width: ${MOBILE_BREAKPOINT - 1}px)`)
    const onChange = () => {
      setIsMobile(window.innerWidth < MOBILE_BREAKPOINT)
    }
    mql.addEventListener("change", onChange)
    setIsMobile(window.innerWidth < MOBILE_BREAKPOINT)
    return () => mql.removeEventListener("change", onChange)
  }, [])

  return !!isMobile
}

client/src/hooks/use-toast.ts.content:
import * as React from "react"

import type {
  ToastActionElement,
  ToastProps,
} from "@/components/ui/toast"

const TOAST_LIMIT = 1
const TOAST_REMOVE_DELAY = 1000000

type ToasterToast = ToastProps & {
  id: string
  title?: React.ReactNode
  description?: React.ReactNode
  action?: ToastActionElement
}

const actionTypes = {
  ADD_TOAST: "ADD_TOAST",
  UPDATE_TOAST: "UPDATE_TOAST",
  DISMISS_TOAST: "DISMISS_TOAST",
  REMOVE_TOAST: "REMOVE_TOAST",
} as const

let count = 0

function genId() {
  count = (count + 1) % Number.MAX_SAFE_INTEGER
  return count.toString()
}

type ActionType = typeof actionTypes

type Action =
  | {
      type: ActionType["ADD_TOAST"]
      toast: ToasterToast
    }
  | {
      type: ActionType["UPDATE_TOAST"]
      toast: Partial<ToasterToast>
    }
  | {
      type: ActionType["DISMISS_TOAST"]
      toastId?: ToasterToast["id"]
    }
  | {
      type: ActionType["REMOVE_TOAST"]
      toastId?: ToasterToast["id"]
    }

interface State {
  toasts: ToasterToast[]
}

const toastTimeouts = new Map<string, ReturnType<typeof setTimeout>>()

const addToRemoveQueue = (toastId: string) => {
  if (toastTimeouts.has(toastId)) {
    return
  }

  const timeout = setTimeout(() => {
    toastTimeouts.delete(toastId)
    dispatch({
      type: "REMOVE_TOAST",
      toastId: toastId,
    })
  }, TOAST_REMOVE_DELAY)

  toastTimeouts.set(toastId, timeout)
}

export const reducer = (state: State, action: Action): State => {
  switch (action.type) {
    case "ADD_TOAST":
      return {
        ...state,
        toasts: [action.toast, ...state.toasts].slice(0, TOAST_LIMIT),
      }

    case "UPDATE_TOAST":
      return {
        ...state,
        toasts: state.toasts.map((t) =>
          t.id === action.toast.id ? { ...t, ...action.toast } : t
        ),
      }

    case "DISMISS_TOAST": {
      const { toastId } = action

      // ! Side effects ! - This could be extracted into a dismissToast() action,
      // but I'll keep it here for simplicity
      if (toastId) {
        addToRemoveQueue(toastId)
      } else {
        state.toasts.forEach((toast) => {
          addToRemoveQueue(toast.id)
        })
      }

      return {
        ...state,
        toasts: state.toasts.map((t) =>
          t.id === toastId || toastId === undefined
            ? {
                ...t,
                open: false,
              }
            : t
        ),
      }
    }
    case "REMOVE_TOAST":
      if (action.toastId === undefined) {
        return {
          ...state,
          toasts: [],
        }
      }
      return {
        ...state,
        toasts: state.toasts.filter((t) => t.id !== action.toastId),
      }
  }
}

const listeners: Array<(state: State) => void> = []

let memoryState: State = { toasts: [] }

function dispatch(action: Action) {
  memoryState = reducer(memoryState, action)
  listeners.forEach((listener) => {
    listener(memoryState)
  })
}

type Toast = Omit<ToasterToast, "id">

function toast({ ...props }: Toast) {
  const id = genId()

  const update = (props: ToasterToast) =>
    dispatch({
      type: "UPDATE_TOAST",
      toast: { ...props, id },
    })
  const dismiss = () => dispatch({ type: "DISMISS_TOAST", toastId: id })

  dispatch({
    type: "ADD_TOAST",
    toast: {
      ...props,
      id,
      open: true,
      onOpenChange: (open) => {
        if (!open) dismiss()
      },
    },
  })

  return {
    id: id,
    dismiss,
    update,
  }
}

function useToast() {
  const [state, setState] = React.useState<State>(memoryState)

  React.useEffect(() => {
    listeners.push(setState)
    return () => {
      const index = listeners.indexOf(setState)
      if (index > -1) {
        listeners.splice(index, 1)
      }
    }
  }, [state])

  return {
    ...state,
    toast,
    dismiss: (toastId?: string) => dispatch({ type: "DISMISS_TOAST", toastId }),
  }
}

export { useToast, toast }

